import React, { useState } from 'react';
import axios from 'axios';
import './qr_g.css';

const QRCodeGenerator = () => {
  const [content, setContent] = useState('');
  const [qrCode, setQRCode] = useState('');
  const [error, setError] = useState('');
  const [contentType, setContentType] = useState('text'); // Default content type

  const handleContentTypeChange = (event) => {
    setContentType(event.target.value);
    setContent(''); // Clear content input when content type changes
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      let formData = new FormData();
      formData.append('type', contentType);

      if (contentType === 'text') {
        formData.append('content', content);
      } else if (contentType === 'image' || contentType === 'file') {
        formData.append('file', content);
      }

      // Send POST request to Flask backend
      const response = await axios.post('https://your-backend-api/generate_qr', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        },
        responseType: 'blob', // Ensure response is treated as a blob
      });

      // Convert blob to base64 string
      const qrCodeBase64 = await convertBlobToBase64(response.data);
      const qrCodeSrc = `data:image/png;base64,${qrCodeBase64}`;

      // Display the QR code image
      setQRCode(qrCodeSrc);
      setError('');
    } catch (error) {
      console.error('Error generating QR code:', error);
      setError('Failed to generate QR code. Please try again.');
    }
  };

  const convertBlobToBase64 = (blob) => {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.onerror = reject;
      reader.onload = () => {
        resolve(reader.result.split(',')[1]);
      };
      reader.readAsDataURL(blob);
    });
  };

  return (
    <div className="qrcode-container">
      <h2>QR Code Generator</h2>
      <form className="qrcode-form" onSubmit={handleSubmit}>
        <label>
          Select content type:
          <select value={contentType} onChange={handleContentTypeChange}>
            <option value="text">Text</option>
            <option value="image">Image URL</option>
            <option value="file">Upload File</option>
          </select>
        </label>
        {contentType !== 'file' && (
          <label>
            Enter {contentType === 'text' ? 'Text' : 'URL'}:
            <input
              type="text"
              value={content}
              onChange={(e) => setContent(e.target.value)}
              required
              placeholder={`Enter ${contentType === 'text' ? 'text' : 'URL'}`}
            />
          </label>
        )}
        {contentType === 'file' && (
          <label>
            Upload File:
            <input
              type="file"
              onChange={(e) => setContent(e.target.files[0])}
              accept=".jpg,.jpeg,.png,.gif,.txt,.pdf,.doc,.docx"
              required
            />
          </label>
        )}
        <button type="submit">Generate QR Code</button>
        {error && <p className="error-message">{error}</p>}
      </form>
      {qrCode && (
        <div className="qrcode-generated">
          <h3>Generated QR Code:</h3>
          <img src={qrCode} alt="Generated QR Code" className="qr-image" />
          <button className="share-button" onClick={() => navigator.clipboard.writeText(qrCode)}>Share the QR code</button>
        </div>
      )}
    </div>
  );
};

export default QRCodeGenerator;
