import React, { useState } from 'react';
import axios from 'axios';
import './qr_g.css';  // Import your CSS file here

const QRCodeGenerator = () => {
  const [url, setUrl] = useState('');
  const [qrCode, setQRCode] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      // Send POST request to Flask backend
      const response = await axios.post('http://localhost:5000/generate_qr', { url }, {
        responseType: 'blob',  // Ensure response is treated as a blob
      });

      // Convert blob to base64 string
      const qrCodeBase64 = await convertBlobToBase64(response.data);
      const qrCodeSrc = `data:image/png;base64,${qrCodeBase64}`;

      // Display the QR code image
      setQRCode(qrCodeSrc);
      setError('');
    } catch (error) {
      console.error('Error generating QR code:', error);
      setError('Failed to generate QR code. Please try again.');
    }
  };

  const convertBlobToBase64 = (blob) => {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.onerror = reject;
      reader.onload = () => {
        resolve(reader.result.split(',')[1]);
      };
      reader.readAsDataURL(blob);
    });
  };

  return (
    <div className="qrcode-container">
      <h2>QR Code Generator</h2>
      <form className="qrcode-form" onSubmit={handleSubmit}>
        <label>
          Enter URL:
          <input
            type="text"
            value={url}
            onChange={(e) => setUrl(e.target.value)}
            required
          />
        </label>
        <button type="submit">Generate QR Code</button>
        {error && <p className="error-message">{error}</p>}
      </form>
      {qrCode && (
        <div className="qrcode-generated">
          <h3>Generated QR Code:</h3>
          <img src={qrCode} alt="Generated QR Code" style={{ width: '150px', height: '150px', margin: '20px auto' }} />
        </div>
      )}
    </div>
  );
};

export default QRCodeGenerator;
