import React, { useState } from 'react';
import axios from 'axios';
import './qr_g.css';

const QRCodeGenerator = () => {
  const [url, setUrl] = useState('');
  const [qrCode, setQRCode] = useState('');
  const [error, setError] = useState('');
  const [message, setMessage] = useState('');

  const handleSubmit = async (event) => {
    event.preventDefault();

    if (!url.trim()) {
      setError('Please enter a valid URL.');
      return;
    }

    try {
      // Send POST request to Flask backend
      const response = await axios.post('http://localhost:5000/generate_qr', { url }, {
        responseType: 'arraybuffer',  // Ensure response is treated as binary data
      });

      // Convert binary data to base64 string
      const qrCodeBase64 = Buffer.from(response.data, 'binary').toString('base64');
      const qrCodeSrc = `data:image/png;base64,${qrCodeBase64}`;

      // Display the QR code image
      setQRCode(qrCodeSrc);
      setMessage('');
      setError('');
    } catch (error) {
      console.error('Error generating QR code:', error);
      setError('Failed to generate QR code. Please try again.');
    }
  };

  return (
    <div className="qrcode-container">
      <h2>QR Code Generator</h2>
      <form className="qrcode-form" onSubmit={handleSubmit}>
        <label>
          Enter URL:
          <input
            type="text"
            value={url}
            onChange={(e) => setUrl(e.target.value)}
            required
          />
        </label>
        <button type="submit">Generate QR Code</button>
        {error && <p className="error-message">{error}</p>}
      </form>
      {qrCode && (
        <div className="qrcode-generated">
          <h3>Generated QR Code:</h3>
          <img src={qrCode} alt="Generated QR Code" />
        </div>
      )}
      {message && (
        <div className="message-container">
          <h3>Server Response:</h3>
          <p>{message}</p>
        </div>
      )}
    </div>
  );
};

export default QRCodeGenerator;
